import React from 'react';
import config from '../config';
import CafeTitle from '../CafeTitle/CafeTitle';

class CafeContainer extends React.Component{
    refreshInterval = 10;
    constructor(props){
        super(props);
        this.state = {
            cafes: [],
            error: null,
            refreshTime: this.refreshInterval
        };
        this.intervalHandle = setInterval(this.tick, 1000);
    }
    componentDidMount() {
        // 1. Load the JavaScript client library.
        window.gapi.load("client", this.initClient);
    }
    initClient = () => {
        // 2. Initialize the JavaScript client library.
        window.gapi.client
            .init({
                apiKey: config.apiKey,
                // Your API key will be automatically added to the Discovery Document URLs.
                discoveryDocs: config.discoveryDocs
            })
            .then(() => {
                // 3. Initialize and make the API request.
                load(this.onLoad);
            });
    };
    onLoad = (data, error) => {
        if (data) {
            const cafes = data.cafes;
            this.setState({ cafes });
        } else {
            this.setState({ error });
        }
    };

    tick = () => {
        let refreshTime = this.state.refreshTime - 1;
        if (refreshTime <= 0) {
            refreshTime = this.refreshInterval;
            load(this.onLoad);
        }
        this.setState({refreshTime})

    };
    render() {
        const {error} = this.state;
        if (error) {
            console.log(error);
        }
        console.log(this.state);
        return (
            <CafeTitle {...this.state} />
        );
    }
}


export function load(callback) {
  window.gapi.client.load("sheets", "v4", () => {
    window.gapi.client.sheets.spreadsheets.values
      .get({
          spreadsheetId: config.spreadsheetId,
          range: "Sheet4!A3:E",
      })
      .then(
        response => {
            console.log(response);
          const data = response.result.values || [];
          const cafes = data.map(cafe => ({
              time: cafe[0],
              name: cafe[1],
              drink: cafe[2],
              isReady: (cafe[3] === 'TRUE'),
              hasReceived: (cafe[4] === 'TRUE'),

          })) || [];
          callback({
            cafes
          });
        },
        response => {
          callback(false, response.result.error);
        }
      );
  });
}

export default CafeContainer;
